import React from 'react';
import ReactECharts from 'echarts-for-react';
const datademo =require("./else");
let han=0;
let shang=0;
let zhou=0;
let qin=0;
let tang=0;
let nanbei=0;
let song=0;
let yuan=0;
let ming=0;
let qing=0;
for(var i=0;i<datademo.length;i++){
  if(datademo[i].年代1==='汉')han++;
  if(datademo[i].年代1==='商')shang++;
  if(datademo[i].年代1==='周')zhou++;
  if(datademo[i].年代1==='秦')qin++;
  if(datademo[i].年代1==='唐')tang++;
  if(datademo[i].年代1==='南北朝')nanbei++;
  if(datademo[i].年代1==='宋')song++;
  if(datademo[i].年代1==='元')yuan++;
  if(datademo[i].年代1==='明')ming++;
  if(datademo[i].年代1==='清')qing++;
}

class Pie extends React.Component{
    constructor(props){
        super(props);
        this.state={
            option : {
                title: {
                  text: '朝代与数量关系',
                  left: 'center'
                },
                tooltip: {
                  trigger: 'item'
                },
                legend: {
                  orient: 'vertical',
                  left: 'left'
                },
                series: [
                  {
                    // name: 'Access From',
                    type: 'pie',
                    radius: '60%',
                    data: [
                      { value: shang, name: '商' },
                      { value: zhou, name: '周' },
                      { value: qin, name: '秦' },
                      { value: han, name: '汉' },
                      { value: tang, name: '唐' },
                      { value: nanbei, name: '南北朝' },
                      { value: song, name: '宋' },
                      { value: yuan, name: '元' },
                      { value: ming, name: '明' },
                      { value: qing, name: '清' }
                    ],
                    emphasis: {
                      itemStyle: {
                        shadowBlur: 10,
                        shadowOffsetX: 0,
                        shadowColor: 'rgba(0, 0, 0, 0.5)'
                      }
                    }
                  }
                ]
              }
        }
    }
    render() {
	
        const option =this.state.option;
        
        return (
          <ReactECharts
          option={option}
          style={{ height: 400 }}
          opts={{ renderer: 'svg' }}
        />
        )
      }
}
export default Pie;
