import React from 'react';
import ReactECharts from 'echarts-for-react';
import Pie from "./pie";
import { Layout, Menu, Breadcrumb,Button,Space,Card} from 'antd';

const datademo =require("./else");
let han=0;
let shang=0;
let zhou=0;
let qin=0;
let tang=0;
let nanbei=0;
let song=0;
let yuan=0;
let ming=0;
let qing=0;
for(var i=0;i<datademo.length;i++){
  if(datademo[i].年代1==='汉')han++;
  if(datademo[i].年代1==='商')shang++;
  if(datademo[i].年代1==='周')zhou++;
  if(datademo[i].年代1==='秦')qin++;
  if(datademo[i].年代1==='唐')tang++;
  if(datademo[i].年代1==='南北朝')nanbei++;
  if(datademo[i].年代1==='宋')song++;
  if(datademo[i].年代1==='元')yuan++;
  if(datademo[i].年代1==='明')ming++;
  if(datademo[i].年代1==='清')qing++;
}


class Page extends React.Component{
  
  constructor(props){
    super(props);
    this.state={
      chooseChart:1,
     // type:'bar',
      option :{
        title: {
          text: '朝代与数量关系'
        },
        tooltip: {},
        legend: {
          // data:['销量']
        },
        xAxis: {
          // data: ['衬衫', '羊毛衫', '雪纺衫', '裤子', '高跟鞋', '袜子']
         data:['商','周','秦','汉','唐','南北朝','宋','元','明','清']
        //  data:['商','周','秦','汉']
        },
        yAxis: {},
        series: [{
          // name: '销量',
          name:'',
          type: 'bar',
          // data: [5, 20, 36, 10, 10, 20]
          data:[shang,zhou,qin,han,tang,nanbei,song,yuan,ming,qing]
        }]
      }
    }
  }
  render() {
	
    const option =this.state.option;
    const chooseChart=this.state.chooseChart;
    const type=this.state.type;
    // if(chooseChart==1){
    //   this.setState({type:'bar'});
    // }
    // if(chooseChart==2){
    //   this.setState({type:'line'});
    // }
    // if(chooseChart==4){
    //   this.setState({type:'scatter'});
    // }
    return (
  <Layout>
    <Space size='large'>
   <Button onClick={()=>this.setState({type:'bar'})}>柱状图</Button>
   <Button onClick={()=>this.setState({type:'line'})}>折线图</Button> 
   {/* <Button onClick={()=>this.setState({chooseChart:3})}>饼图</Button> */}
  <Button onClick={()=>this.setState({type:'scatter'})}>散点图</Button>
                  
  </Space>
  <ReactECharts
      option={option}
      style={{ height: 400 }}
      opts={{ renderer: 'svg' }}
    />
  </Layout>  
    )
  }
}


export default Page;
