import React from 'react';
import ReactDOM from 'react-dom';
import 'antd/dist/antd.css';
import './index.css';
import { Layout, Menu, Breadcrumb,Button,Space,Card,Select,Checkbox, Row, Col} from 'antd';
import { UserOutlined, LaptopOutlined, NotificationOutlined } from '@ant-design/icons';
import Pie from "./echarts/pie";
import Page from "./echarts/page";
import Page2 from "./echarts/page2";
import Listdemo from "./listdemo"
import { Routes,BrowserRouter as Router,Route,Link } from 'react-router-dom';

const { SubMenu } = Menu;
const { Header, Content, Sider } = Layout;
const { Option } = Select;

// yAxis=[shang,zhou,qin,han,tang,nanbei,song,yuan,ming,qing]
const elsedata =require("./echarts/else");
const gugongdata=require("./echarts/gugong")
function GetY(){
  let han=0;
  let shang=0;
  let zhou=0;
  let qin=0;
  let tang=0;
  let nanbei=0;
  let song=0;
  let yuan=0;
  let ming=0;
  let qing=0;
  for(var i=0;i<elsedata.length;i++){
    if(elsedata[i].年代1==='汉')han++;
    if(elsedata[i].年代1==='商')shang++;
    if(elsedata[i].年代1==='周')zhou++;
    if(elsedata[i].年代1==='秦')qin++;
    if(elsedata[i].年代1==='唐')tang++;
    if(elsedata[i].年代1==='南北朝')nanbei++;
    if(elsedata[i].年代1==='宋')song++;
    if(elsedata[i].年代1==='元')yuan++;
    if(elsedata[i].年代1==='明')ming++;
    if(elsedata[i].年代1==='清')qing++;
  }
  return [shang,zhou,qin,han,tang,nanbei,song,yuan,ming,qing]
}


class Chartdemo extends React.Component {
  constructor(props) {
    super(props);
    this.state={
      chooseChart:1,
      yAxis:<GetY/>,
    };
  }


  render() {
    
    var chooseChart=this.state.chooseChart;
    var yAxis=this.state.yAxis;
    var chart=null;
    
    if(chooseChart==1){

      chart=<Page key={1} type='bar' data={yAxis}/>;
    }
    if(chooseChart==2){
      chart=<Page key={2} type='line' data={yAxis}/>; 
    }
	if(chooseChart==3){
      chart=<Pie />; 
    }
	if(chooseChart==4){
      chart=<Page key={4} type='scatter' data={yAxis}/>; 
    }
    return (

        <div>
                <Space>
                <Button onClick={()=>this.setState({chooseChart:1})}>柱状图</Button>
				          <Button onClick={()=>this.setState({chooseChart:2})}>折线图</Button> 
                  <Button onClick={()=>this.setState({chooseChart:3})}>饼图</Button>
                  <Button onClick={()=>this.setState({chooseChart:4})}>散点图</Button>
                </Space>
                <br/>
                <Space>
                  <Select width='80px' defaultValue='elsedata'>
                    <Select.Option value='elsedata' >其他</Select.Option>
                    <Select.Option value='gugongdata'>故宫博物院</Select.Option>
                  </Select>
                </Space>
                <Card style={{ width: 700 }}>
                  {chart}
                  
                </Card>

        
      </div>
      
    )

  }
}
export default Chartdemo;
