import React from 'react';
import ReactDOM from 'react-dom';
import 'antd/dist/antd.css';
import './index.css';
import { Layout, Menu, Breadcrumb,Button,Space,Card,Select,Checkbox, Row, Col} from 'antd';
import { UserOutlined, LaptopOutlined, NotificationOutlined } from '@ant-design/icons';
import Histogram from "./histogram";
import Page from "./echarts/charts";
//import LineCharts from "./echarts/linecharts";
//import PieCharts from "./echarts/barcharts";
//import ScattleCharts from "./echarts/scattercharts";

const { SubMenu } = Menu;
const { Header, Content, Sider } = Layout;
const { Option } = Select;


class Chartdemo extends React.Component {
  constructor(props) {
    super(props);
    //this.handleChartClick=this.handleBarClick.bind(this);
    //this.handleLineClick=this.handleLineClick.bind(this);
	//this.handlePieClick=this.handlePieClick.bind(this);
    //this.handleScatterClick=this.handleScatterClick.bind(this);
	this.handleC1Click=this.handleC1Click.bind(this);
	this.handleC2Click=this.handleC2Click.bind(this);
	//this.handleC1Click=this.handleC1Click.bind(this);
	//this.handleC1Click=this.handleC1Click.bind(this);
    this.onChange=this.onChange.bind(this);
    this.state={
      chooseChart:1,
      
    };
  }
  handleC1Click(){
    this.setState({chooseChart:1});
  }
  handleC2Click(){
    this.setState({chooseChart:2});
  }
  /*handleC3Click(){
    this.setState({chooseChart:3});
  }
  handleC4Click(){
    this.setState({chooseChart:4});
  }
  */
  onChange(checkedValues) {
    console.log('checked = ', checkedValues);
    
  }
  render() {
    chooseChart=this.state.chooseChart;
    let chart=null;
    if(chooseChart==1){
      chart=<Page type='line'/>;
    }
    else if(chooseChart==2){
      chart=<Page type='bar'/>;
    }
    return (
        <div>
        <div>
        <Layout>
          <Header className="header">
            <div className="logo"/>
            <Menu theme="dark" mode="horizontal" defaultSelectedKeys={['2']}>
              <Menu.Item key="1">nav 1</Menu.Item>
              <Menu.Item key="2">nav 2</Menu.Item>
              <Menu.Item key="3">nav 3</Menu.Item>
            </Menu>
          </Header>
          <Layout>
            <Sider width={200} className="site-layout-background">
              <Menu
                  mode="inline"
                  defaultSelectedKeys={['1']}
                  defaultOpenKeys={['sub1']}
                  style={{height: '100%', borderRight: 0}}
              >
                <SubMenu key="sub1" icon={<UserOutlined/>} title="subnav 1">
                  <Menu.Item key="1">option1</Menu.Item>
                  <Menu.Item key="2">option2</Menu.Item>
                  <Menu.Item key="3">option3</Menu.Item>
                  <Menu.Item key="4">option4</Menu.Item>
                </SubMenu>
                <SubMenu key="sub2" icon={<LaptopOutlined/>} title="subnav 2">
                  <Menu.Item key="5">option5</Menu.Item>
                  <Menu.Item key="6">option6</Menu.Item>
                  <Menu.Item key="7">option7</Menu.Item>
                  <Menu.Item key="8">option8</Menu.Item>
                </SubMenu>
                <SubMenu key="sub3" icon={<NotificationOutlined/>} title="subnav 3">
                  <Menu.Item key="9">option9</Menu.Item>
                  <Menu.Item key="10">option10</Menu.Item>
                  <Menu.Item key="11">option11</Menu.Item>
                  <Menu.Item key="12">option12</Menu.Item>
                </SubMenu>
              </Menu>
            </Sider>
            <Layout style={{padding: '0 24px 24px'}}>
              <Breadcrumb style={{margin: '16px 0'}}>
                <Breadcrumb.Item>Home</Breadcrumb.Item>
                <Breadcrumb.Item>List</Breadcrumb.Item>
                <Breadcrumb.Item>App</Breadcrumb.Item>
              </Breadcrumb>
              <Content
                  className="site-layout-background"
                  style={{
                    padding: 24,
                    margin: 0,
                    minHeight: 280,
                  }}
              >
                <h1 style={{fontSize:'40px'}}>
                  图表分析选项
                </h1>
                
                <Space size='large'>
                  <Button onClick={()=>this.handleC1Click()}>柱状图</Button>
                  <Button onClick={()=>this.handleC2Click()}>折线图</Button> 
				  //<Button onClick={this.setState({chooseChart:2})}>折线图</Button> 
                  <Button onClick={()=>this.handleC3Click()}>饼图</Button>
                  <Button onClick={()=>this.handleC4Click()}>散点图</Button>
                  
                </Space>
                <br/><br/>
                <Space>
                <Checkbox.Group style={{ width: '100%' }} onChange={()=>this.onChange()}>
                  <Row>
                    <Col >
                      <Checkbox value="夏">夏</Checkbox>
                    </Col>
                    <Col>
                      <Checkbox value="商">商</Checkbox>
                    </Col>
                    <Col>
                      <Checkbox value="周">周</Checkbox>
                    </Col>
                    <Col >
                      <Checkbox value="秦">秦</Checkbox>
                    </Col>
                    <Col >
                      <Checkbox value="汉">汉</Checkbox>
                    </Col>
                  </Row>
                </Checkbox.Group>
                </Space>
                
                <Card style={{ width: 700 }}>
                  {chart}
                </Card>
              
              </Content>
            </Layout>
          </Layout>
        </Layout>
      </div>
      
      
        </div>
    )

  }
}
export default Chartdemo;
