import React from 'react';
import ReactECharts from 'echarts-for-react';


const datademo =require("./else");
let han=0;
for(var i=0;i<datademo.length;i++){
  if(datademo[i].年代1==='汉')han++;
}


class Page extends React.Component{
  
  constructor(props){
    super(props);
	this.initchart = this.initchart.bind(this);
	this.state={
      option:null    
   };
   this.initchart();
  }
  initchart() {
    this.option={
        title: {
          text: '朝代与数量关系'
        },
        tooltip: {},
        legend: {
          data:['销量']
        },
        xAxis: {
          // data: ['衬衫', '羊毛衫', '雪纺衫', '裤子', '高跟鞋', '袜子']
         // data:['夏','商','周','秦','汉','唐','南北朝','宋','元','明','清']
         data:['夏','商','周','秦','汉']
        },
        yAxis: {},
        series: [{
          // name: '销量',
          name:'',
          type: this.props.ttt,  //'bar',
          // data: [5, 20, 36, 10, 10, 20]
          data:[0,0,0,0,han]
        }]
      }
	}
  
  componentDidMount() {
    this.initchart();
  }
  
  componentDidUpdate() {
    this.initchart();
  }
  render() {
    const option =this.state.option;
    
    return (
      <ReactECharts
      option={option}
      style={{ height: 400 }}
      opts={{ renderer: 'svg' }}
    />
    )
  }
}


export default Page;
