import React from 'react'
import mapboxgl from 'mapbox-gl';
const datasource=require("./datasource");
var data=[];



class MyMap extends React.Component {
  constructor() {
    super();
  }

  componentDidMount() {

    mapboxgl.accessToken = 'pk.eyJ1IjoiemhhbmdqaW5neXVhbiIsImEiOiJja2R5cHhoNXYycGVtMnlteXkwZGViZDc2In0.UhckH-74AgPwMsDhPjparQ';

    const map = new mapboxgl.Map({
    container: this.mapContainer,
    style: 'mapbox://styles/mapbox/satellite-v9',
    center: [110, 30],
    zoom: 3,
    scrollZoom: false,
    });
    map.on('load', function () {        /* on(type,callback)为type事件添加监听器，callback是响应函数*/
      map.addSource("points", {
          "type": "geojson",          /* geojson类型资源 */
          "data": {                   /* geojson数据 */
              "type": "FeatureCollection",
              "features": [
              //   {
              //     "type": "Feature",
              //     "geometry": {
              //         "type": "Point",
              //         "coordinates": [77.03238901390978, 38.913188059745586]
              //     },
              //     "properties": {
              //         "title": "Mapbox DC",
              //         "icon": "circle"
              //     }
              // },
               {
                  "type": "Feature",
                  "geometry": {
                      "type": "Point",
                      "coordinates": [122.414, 37.776]
                  },
                  "properties": {
                      "title": "Mapbox SF",
                      "icon": "circle"
                  }
              }]
          }
      });

      map.addLayer({
          "id": "points",
          "type": "symbol",   /* symbol类型layer，一般用来绘制点*/
          "source": "points",
          "layout": {
            "icon-image": "pulsing-dot",   /* 图标图像 */
            "text-field": "{title}",     /* 使用text标签显示的值，Feature属性使用{域名}格式*/
            "text-font": ["Open Sans Semibold", "Arial Unicode MS Bold"],  /* 文本字体 */
            "text-offset": [0, 0.6],     /* 该属性表示文本偏移锚点的距离，正值表示向右和向下，负值表示向左和向上 */
            "text-anchor": "top"         /* 该属性设置文本中最接近锚点的部分，top表示文本的顶端放置到最接近锚点的位置 */
        }
      });
  });

    var nav = new mapboxgl.NavigationControl();
    map.addControl(nav, 'top-left');
  }

  render() { 
    return (
      <div>
        {/* 引入样式文件 */}
        <link href='https://api.mapbox.com/mapbox-gl-js/v1.12.0/mapbox-gl.css' rel='stylesheet' />
        {/* 地图容器 */}
        <div ref={el => this.mapContainer = el} style={{height: '100vh', width:'100vw', position: 'absolute'}} />
      </div>
    );
  }
}

export default MyMap;
