import React from 'react';
import ReactDOM from 'react-dom';
import 'antd/dist/antd.css';
import './index.css';
import { Layout, Menu, Breadcrumb,Button,Space,Card,Select} from 'antd';
import { UserOutlined, LaptopOutlined, NotificationOutlined } from '@ant-design/icons';
import Histogram from "./histogram";
import Page from "./page";
import Locale from "./locale";
import Detademo from './datademo';
const { SubMenu } = Menu;
const { Header, Content, Sider } = Layout;

class Chartdemo extends React.Component {
  constructor(props) {
    super(props);
    this.handlePageClick=this.handlePageClick.bind(this);
    this.handleLocaleClick=this.handleLocaleClick.bind(this);
    this.state={
      chooseChart:1,
      Xtpye:'',
      Ytype:''
    };
  }
  handlePageClick(){
    this.setState({chooseChart:1});
  }
  handleLocaleClick(){
    this.setState({chooseChart:2});
  }
  handleXYTypeClick(){
    alert(this.ref.Xtpye.state)
  }
  render() {
    const chooseChart=this.state.chooseChart;
    let chart=null;
    if(chooseChart==1){
      chart=<Page/>;
    }
    else if(chooseChart==2){
      chart=<Locale/>;
    }
    return (
        <div>
        <div>
        <Layout>
          <Header className="header">
            <div className="logo"/>
            <Menu theme="dark" mode="horizontal" defaultSelectedKeys={['2']}>
              <Menu.Item key="1">nav 1</Menu.Item>
              <Menu.Item key="2">nav 2</Menu.Item>
              <Menu.Item key="3">nav 3</Menu.Item>
            </Menu>
          </Header>
          <Layout>
            <Sider width={200} className="site-layout-background">
              <Menu
                  mode="inline"
                  defaultSelectedKeys={['1']}
                  defaultOpenKeys={['sub1']}
                  style={{height: '100%', borderRight: 0}}
              >
                <SubMenu key="sub1" icon={<UserOutlined/>} title="subnav 1">
                  <Menu.Item key="1">option1</Menu.Item>
                  <Menu.Item key="2">option2</Menu.Item>
                  <Menu.Item key="3">option3</Menu.Item>
                  <Menu.Item key="4">option4</Menu.Item>
                </SubMenu>
                <SubMenu key="sub2" icon={<LaptopOutlined/>} title="subnav 2">
                  <Menu.Item key="5">option5</Menu.Item>
                  <Menu.Item key="6">option6</Menu.Item>
                  <Menu.Item key="7">option7</Menu.Item>
                  <Menu.Item key="8">option8</Menu.Item>
                </SubMenu>
                <SubMenu key="sub3" icon={<NotificationOutlined/>} title="subnav 3">
                  <Menu.Item key="9">option9</Menu.Item>
                  <Menu.Item key="10">option10</Menu.Item>
                  <Menu.Item key="11">option11</Menu.Item>
                  <Menu.Item key="12">option12</Menu.Item>
                </SubMenu>
              </Menu>
            </Sider>
            <Layout style={{padding: '0 24px 24px'}}>
              <Breadcrumb style={{margin: '16px 0'}}>
                <Breadcrumb.Item>Home</Breadcrumb.Item>
                <Breadcrumb.Item>List</Breadcrumb.Item>
                <Breadcrumb.Item>App</Breadcrumb.Item>
              </Breadcrumb>
              <Content
                  className="site-layout-background"
                  style={{
                    padding: 24,
                    margin: 0,
                    minHeight: 280,
                  }}
              >
                <h1 style={{fontSize:'40px'}}>
                  图表分析选项
                </h1>
                <Space size='large'>
                  <Button onClick={()=>this.handlePageClick()}>柱状图</Button>
                  <Button onClick={()=>this.handleLocaleClick()}>折线图</Button>
                  <Button >饼图</Button>
                  <Button >散点图</Button>
                  
                </Space>
                <br/><br/>
                <Space>
                关系：
                <Select ref='Ytype'>
                <Select.Option value="name">name</Select.Option>
                </Select>
                与
                <Select ref='Xtype'>
                <Select.Option value="age">age</Select.Option>
                </Select>
                <Button>确定</Button>
                </Space>
                <Card style={{ width: 700 }}>
                  {chart}
                </Card>
              
              </Content>
            </Layout>
          </Layout>
        </Layout>
      </div>
        </div>
    )

  }
}
export default Chartdemo;
