import React from 'react';
import ReactECharts from 'echarts-for-react';



class Page2 extends React.Component{
  
  constructor(props){
    super(props);
    this.state={
      option :{
        title: {
          text: '地区与数量关系'
        },
        tooltip: {},
        legend: {
         
        },
        xAxis: {
         data:[]
        },
        yAxis: {},
        series: [{
          name:'',
          type: this.props.type,
          data:this.props.data
        }]
      }
    }
  }
  render() {
	
    const option =this.state.option;
    
    return (
      <ReactECharts
      option={option}
      style={{ height: 400 }}
      opts={{ renderer: 'svg' }}
    />
    )
  }
}


export default Page2;
