class Chartdemo4 extends React.Component {
    constructor(props) {
        super(props);
        this.state={
          chooseChart:'1',
          yAxis:yAxisData,
        };
      }
    render(){
        var chooseChart=this.state.chooseChart;
        var yAxis=this.state.yAxis;
        var chart=null;
        if(chooseChart=='1'){
    
            chart=<Page4 key={'1'} type='bar' data={yAxis}/>;
          }
          if(chooseChart=='2'){
            chart=<Pie4 key={'2'} data={yAxis}/>; 
          }
        //   if(chooseChart=='3'){
