import React from 'react'
import mapboxgl from 'mapbox-gl'


class MyMap2 extends React.Component {
    constructor() {
      super();
    }
  
    componentDidMount() {
    mapboxgl.accessToken = 'pk.eyJ1IjoiemhhbmdqaW5neXVhbiIsImEiOiJja2R5cHhoNXYycGVtMnlteXkwZGViZDc2In0.UhckH-74AgPwMsDhPjparQ'
    const map = new mapboxgl.Map({
    container: this.mapContainer, // 地图容器 id
    style: 'mapbox://styles/mapbox/streets-v11', // 样式
    center: [104.294538, 35.860092], // 地图初始中心点 [经度, 维度]
    zoom: 2.4 // 地图初始缩放级别
    });

    map.on('load', function () { 
      map.addSource('trees', {
        type: 'geojson',
        data: './data.geojson'
        });
        map.addLayer({
          id: 'trees-heat',
          type: 'heatmap',
          source: 'trees',
          maxzoom: 15,
          paint: {
          // increase weight as diameter breast height increases
          'heatmap-weight': {
          property: 'dbh',
          type: 'exponential',
          stops: [
          [1, 0],
          [62, 1]
          ]
          },
          // increase intensity as zoom level increases
          'heatmap-intensity': {
          stops: [
          [11, 1],
          [15, 3]
          ]
          },
          // assign color values be applied to points depending on their density
          'heatmap-color': [
          'interpolate',
          ['linear'],
          ['heatmap-density'],
          0, 'rgba(236,222,239,0)',
          0.2, 'rgb(208,209,230)',
          0.4, 'rgb(166,189,219)',
          0.6, 'rgb(103,169,207)',
          0.8, 'rgb(28,144,153)'
          ],
          // increase radius as zoom increases
          'heatmap-radius': {
          stops: [
          [11, 15],
          [15, 20]
          ]
          },
          // decrease opacity to transition into the circle layer
          'heatmap-opacity': {
          default: 1,
          stops: [
          [14, 1],
          [15, 0]
          ]
          },
          }
          }, 'waterway-label');
        // add circle layer here
       });
      
    var nav = new mapboxgl.NavigationControl();
    map.addControl(nav, 'top-left');
    this.setState({map: map});
    }
    
    render() { 
        return (
          <div>
            {/* 引入样式文件 */}
            <link href='https://api.mapbox.com/mapbox-gl-js/v1.12.0/mapbox-gl.css' rel='stylesheet' />
            {/* 地图容器 */}
            <div ref={el => this.mapContainer = el} style={{height: '90%', width:'95%', position: 'absolute'}} />
          </div>
        );
      }
}
export default MyMap2;
