import logo from './logo.svg';
import './App.css';
import Chartdemo from './chartdemo';
import Listdemo from "./listdemo";
import {Component} from "react";
import Csvdemo from './csvdemo';
class App extends Component{
  state={
    comments:[
      // {id:1,name:'jack',content:'sofa!!'},
      // {id:2,name:'rose',content:'hello!'}
    ],
    userName:'',
    userContent:''
  }
  renderList(){
    return this.state.comments.length===0?
    ( <div className='no-comment'>暂无评论</div>)
    :(<ul>
      {this.state.comments.map(item =>(
        <li key={item.id}>
          <h3>评论人：{item.name}</h3>
      <p>评论内容：{item.content}</p>
        </li>
      ))}
    </ul>)
  }
  handleForm=(e)=>{
    const{name,value}=e.target
    this.setState({[name]:value})
  }
  addComment=()=>{
    const{ comments,userContent,userName}=this.state
    const newComments=[...comments,{id: Math.random(),name:userName,content:userContent}]
    this.setState({comments:newComments})
  }
  render() {
    return(
        <div className='app'>
          <div>
            <input className='user' type='text' placeholder="请输入评论人" 
             value={this.state.userName} name='userName' onChange={this.handleForm}/>
            <br/>
            <textarea className='content' cols='30' rows='10' placeholder='输入评论内容'
             value={this.state.userContent} name='userContent' onChange={this.handleForm}/>
            <br/>
            <button onClick={this.addComment}>发表评论</button>
          </div>
          {
            this.renderList()
          }


        </div>
    )
  }
}

export default App;
